import React, { useState } from "react";
import "./Exercicis/Comptador/Display/display";
import Display from "./Exercicis/Comptador/Display/display";
import "./Exercicis/Comptador/Display/display.css";

function App() {
  const [valor, setValor] = useState(0);

  const sumar = () => {
    setValor(valor + 1);
  };
  const reset = () => {
    setValor(0);
  };
  const restar = () => {
    setValor(valor - 1);
  };
  const sumarCinco = () => {
    setValor(valor + 5);
  };

  return (
    <div className="DisplayContainer">
      <div className="screen">
        <h1>Contador</h1>
        <h2>{valor}</h2>
      </div>
      <div className="buttons">
        <button onClick={sumar}>Aumentar</button>
        <button onClick={reset}>Reiniciar</button>
        <button onClick={restar}>Reducir</button>
        <button onClick={sumarCinco}>+5</button>
      </div>
    </div>
  );
}

export default App;
