import React, { useState } from "react";
import "./Exercicis/Comptador/Display/display";
import Display from "./Exercicis/Comptador/Display/display";
import "./Exercicis/Comptador/Display/display.css";

function App() {
  const [valor, setValor] = useState(0);

  const sumar = () => {
    setValor(valor + 1);
  };
  const reset = () => {
    setValor(0);
  };
  const restar = () => {
    setValor(valor - 1);
  };
  const sumarCinco = () => {
    setValor(valor + 5);
  };

  return (
    <div className="DisplayContainer">
      <div className="title">
        <h1>Contador</h1>
      </div>
      <div className="screen">
        <h2>{valor}</h2>
      </div>
      <div className="buttons">
        <div className="mainButtons">
          <button onClick={sumar}>Aumentar</button>
          <button onClick={restar} disabled={valor === 0}>
            Reducir
          </button>
        </div>
        <div className="otherButtons">
          <button onClick={reset}>Reiniciar</button>
          <button onClick={sumarCinco}>+5</button>
        </div>
      </div>
    </div>
  );
}

export default App;
